CREATE TABLE cursoangular.users (
	id INTEGER,
	name VARCHAR,
	email VARCHAR,
	password VARCHAR,
	remember_token VARCHAR,
	created_at TIMESTAMP,
	updated_at TIMESTAMP
) ;

CREATE SEQUENCE IF NOT EXISTS cursoangular.seq_images;
/*CREATE SEQUENCE IF NOT EXISTS cursoangular.HIBERNATE_SEQUENCE;*/


CREATE TABLE cursoangular.images (
	id BIGINT DEFAULT (NEXT VALUE FOR cursoangular.seq_images) not null primary key,
	title VARCHAR,
	description VARCHAR (1250),
	thumbnail VARCHAR,
	imageLink VARCHAR,
	user_id INT,
	created_at TIMESTAMP,
	updated_at TIMESTAMP
) ;

